// You are given two strings s and t.

// String t is generated by random shuffling string s and then add one more letter at a random position.

// Return the letter that was added to t.

 

// Example 1:

// Input: s = "abcd", t = "abcde"
// Output: "e"
// Explanation: 'e' is the letter that was added.
// Example 2:

// Input: s = "", t = "y"
// Output: "y"

// easy , hash section, 60% acceptance.

var findTheDifference = function(s, t) {
    let sSplit = s.split("");
    let tSplit = t.split("");
    let sCount = {};
    let tCount = {}

    for (let letter of sSplit) {
        sCount[letter] ? sCount[letter] ++ : sCount[letter] = 1;
    }
    for (let letter of tSplit) {
        tCount[letter] ? tCount[letter] ++ : tCount[letter] = 1;
    }
    for (let letter of Object.keys(tCount)) {
        if (!sCount[letter] || sCount[letter] !== tCount[letter]) {
            return letter;
        }
    }
};